package tipos_datos;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//decalracion de tiposd e datos enteros
		long edad=12;
		long edad1=40;
		//decalraciond e tipos de datos decimales
		double nota=20.50;
		
		//decalraciond e tipos de datos CARACTER
		char op='a';
		//decalraciond e tipos de datos BOOLEANOS
		boolean decision=false;
		
		//decalraciond e tipos de datos integer
		Integer numero=null;
		
		//decalraciond e tipos de datos string
		String palabra= "Hola mundo";
		
		//constante
		final double PI=3.141516;
		
		
		System.out.println("La edad de jose es: " + edad);
		System.out.println("La edad de carlos es: " + edad1);
		System.out.println("La nota de carlos es: " + nota);
		System.out.print("La opcion elegida es: " + op);
		System.out.print("La opcion elegida es: " + decision);
		System.out.println("La opcion elegida es: " + numero);
		System.out.print("La opcion elegida es: " + palabra);
		System.out.print("La opcion elegida es: " + PI);
		

	}

}
